import React from "react";
import "./Acceuil.css";
import index from './index'
import Home from "./pages/Home";
import PremierAcCours from "./pages/PremierAcCours";

class Acceuil extends React.Component {

   
    constructor(props) {
        super(props);
        this.cd = this.props.cd;
        this.state = {temperature: ''};
      }
      componentWillUnmount() {
        this.props.cd=""
        this.state.contenu=""
      }

    state = {
        item : [
        {title: "cours1"},
        {title: "cours2"},
        {title: "cours3"},
        {title: "cours4"},
        {title: "cours5"},
        {title: "cours6"}
        ],
        item2 : [
            {title: "Ex1"},
            {title: "Ex2"},
            {title: "Ex3"},
            {title: "Ex4"},
            {title: "Ex5"},
            {title: "Ex6"}
        
        ],
         contenu : "" ,
         cd : ""
        
        }


    


    Test() {
        if(this.props.cd=="/"){

            this.setState( {contenu : this.state.item.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                    </li>
        
                );
            
            }
            )}
            )

        }
        
        
        if(this.props.cd=="/PremierAcCours"){

            this.setState( {contenu : this.state.item2.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                    </li>
        
                );
            
            }
            )}
            )

    
        }

    }
        

  render() {




    return (
      <div>
        
        <div class="list-type5 mt-5">
          <ol>

            {this.Test()}
            {this.state.contenu}
            
          </ol>
        </div>
      </div>
    );

  }

 

}

export default Acceuil;
