import React from "react";
import "./Acceuil.css";
import index from './index'

class Acceuil extends React.Component {

  
    
    state = {
        item : [
        {title: "cours1"},
        {title: "cours2"},
        {title: "cours3"},
        {title: "cours4"},
        {title: "cours5"},
        {title: "cours6"}
        ],
        item2 : [
            {title: "Ex1"},
            {title: "Ex2"},
            {title: "Ex3"},
            {title: "Ex4"},
            {title: "Ex5"},
            {title: "Ex6"}
        
        ],
        item3 :[
            {id : 1 , contenu : ""},
            {id : 2 , contenu : ""},
        ]
        }


    


    Test() {
        if(this.props.cd==this.state.item3.id){
            return (this.state.item.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                    </li>
        
                );
            
            }
            ))
        
            }
        if(this.props.cd==this.state.id.item3.id){
            return (this.state.item2.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                     </li>
        
                );
            
            }
            ))
        }
        }
        

  render() {




    return (
      <div>
        
        <div class="list-type5 mt-5">
          <ol>
            
            {this.Test}
            
          </ol>
        </div>
      </div>
    );
  }

 

}

export default Acceuil;
