        item2 : [
            {title: "Ex1"},
            {title: "Ex2"},
            {title: "Ex3"},
            {title: "Ex4"},
            {title: "Ex5"},
            {title: "Ex6"}
        
        ],
         contenu : "" ,
         cd : ""
        
        }


    


    Test() {
        if(this.state.cd=="/"){

            this.setState( {contenu : this.state.item.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                    </li>
        
                );
            
            }
            )}
            )

        }
        
        
        if(this.state.cd=="/PremierAcCours"){

            this.setState( {contenu : this.state.item2.map( (it) => {
        
                return(
                    <li>
                        <a href="#">{it.title}</a>
                    </li>
        
                );
            
            }
            )}
            )

    
        }

    }
        

  render() {

    console.log("render est appele")


    return (
      <div>
        
        <div class="list-type5 mt-5">
          <ol>

            
          </ol>
        </div>
      </div>
    );

  }

 

}

export default Acceuil;
