import React from "react";
import "./Acceuil.css";
import index from './index'
import Home from "./pages/Home";
import PremierAcCours from "./pages/PremierAcCours";

class Acceuil extends React.Component {

   
    constructor(props) {
        super(props);
        //this.state.cd = this.props.cd;
        

        
      }
      componentWillUnmount() {
    
        console.log("WillUnmount appelé")
      }
      componentDidMount(){
        console.log("didMount appélé")
      }

      componentDidUpdate(){
        console.log("didUpdate appélé")
      }

    state = {
        item : [
        {title: "cours1"},
        {title: "cours2"},
        {title: "cours3"},
        {title: "cours4"},
        {title: "cours5"},
        {title: "cours6"}
        ],
        item2 : [
            {title: "Ex1"},
            {title: "Ex2"},
            {title: "Ex3"},
            {title: "Ex4"},
            {title: "Ex5"},
            {title: "Ex6"}
        
        ],
         contenu : "rf" ,
        }

        

  render() {

    console.log("render est appele")

    if(this.props.pr=="/"){
  
      this.setState( {contenu : this.state.item.map( (it) => {
  
          return(
              <li>
                  <a href="#">{it.title}</a>
              </li>
  
          );
      
      }
      )}
      )

  }
  
  
  if(this.props.pr=="/PremierAcCours"){

      this.setState( {contenu : this.state.item2.map( (it) => {
  
          return(
              <li>
                  <a href="#">{it.title}</a>
              </li>
  
          );
      
      }
      )}
      )


  }

    return (
      <div>
        
        <div class="list-type5 mt-5">
          <ol>
                  {this.state.contenu}
          </ol>
        </div>
      </div>
    );

  }

 

}

export default Acceuil;
